<?	session_start();		include "models/viewModel.php";	include "models/protector.php";	include "helpers/db.php";	include "models/coursesModel.php";	include "models/studentModel.php";	include "models/gradesModel.php";		$view = new viewModel();	$courses = new coursesModel();	$student = new studentModel();	$grades = new gradesModel();		// Header View	$view->getView('views/header.php');		// Content View	if (!empty( $_GET["action"])){		if ( $_GET["action"] == "" ) {			$cid = $_GET["cid"];			$sid = $_GET["sid"];			$grades -> getGrades( $cid, $sid );				} elseif( $_GET["action"] == "savegrades"){												$cid = $_GET["cid"];			$sid = $_GET["sid"];			$assignments = $_POST;						//$grades -> insertGrade($cid, $sid, $assignments);									$cid = $_GET["cid"];			$sid = $_GET["sid"];			//$grades -> getGrades( $cid, $sid );						$grades -> updateGrade( $cid, $sid, $assignments);						$c = $courses -> getCourse($_GET["cid"]);			$s = $student -> getStudent($_GET["sid"]);			$a = $courses -> getAssignments($_GET["cid"]);			$g = $grades ->getGrade($_GET["sid"], $_GET["cid"]);								$view -> getView("views/grades.php", $c, $g, $a, $s);		}	}else{		$c = $courses -> getCourse($_GET["cid"]);		$s = $student -> getStudent($_GET["sid"]);		$a = $courses -> getAssignments($_GET["cid"]);		$g = $grades ->getGrade($_GET["sid"], $_GET["cid"]);				$view -> getView("views/grades.php", $c, $g, $a, $s);	}		// Footer View	$view -> getView("views/footer.php");?>